<?php /* /home/mong/project/memory/template/Common/PageNavigator.tpl */ class Common_PageNavigatorTemplate extends Podo_Template_Base { const HAVE_CDATA = FALSE; public $dblPrevPage; public $callback; public $isShowDblPrevBtn; public $prevPage; public $isShowPrevBtn; public $firstPage; public $lastPage; public $currPage; public $nextPage; public $isShowNextBtn; public $dblNextPage; public $isShowDblNextBtn; public function printHead($p_intLine, $p_intCol) { ?><?php if ($this->isShowDblPrevBtn) { ?><a href="?page=<?php echo $this->_escapeAttrib($this->dblPrevPage); ?>" class="go-first" title="처음페이지로 이동" <?php if (isset($this->callback)) { ?>onclick="try { <?php echo $this->_escapeAttrib($this->callback); ?>(<?php echo $this->_escapeAttrib($this->dblPrevPage); ?>); return false; } catch (e) { }"<?php } ?>>처음페이지</a><?php } ?>
