<span class="bar"></span>

<?php for ($i=$this->firstPage; $i<=$this->lastPage; $i++) { ?>
<?php $this->_isCondResult1 =  ($i != $this->currPage); if ($this->_isCondResult1) { ?><a href="?page=<?php echo $this->_escapeAttrib($i); ?>" class="page-num" title="<?php echo $this->_escapeAttrib($i); ?>페이지" <?php if (isset($this->callback)) { ?>onclick="try { <?php echo $this->_escapeAttrib($this->callback); ?>(<?php echo $this->_escapeAttrib($i); ?>); return false; } catch (e) { }"<?php } ?>><?php } ?><?php $this->_isCondResult2 =  ($i == $this->currPage); if ($this->_isCondResult2) { ?><strong class="this"><?php } ?><?php echo $i; ?><?php if ($this->_isCondResult2) { ?></strong><?php } ?><?php if ($this->_isCondResult1) { ?></a><?php } ?>
<span class="bar"></span>
<?php } ?>

<?php if ($this->isShowNextBtn) { ?><a href="?page=<?php echo $this->_escapeAttrib($this->nextPage); ?>" class="go-forward" title="다음페이지로 이동" <?php if (isset($this->callback)) { ?>onclick="try { <?php echo $this->_escapeAttrib($this->callback); ?>(<?php echo $this->_escapeAttrib($this->nextPage); ?>); return false; } catch (e) { }"<?php } ?>>다음페이지</a><?php } ?>
<?php if ($this->isShowDblNextBtn) { ?><a href="?page=<?php echo $this->_escapeAttrib($this->dblNextPage); ?>" class="go-end" title="마지막페이지로 이동" <?php if (isset($this->callback)) { ?>onclick="try { <?php echo $this->_escapeAttrib($this->callback); ?>(<?php echo $this->_escapeAttrib($this->dblNextPage); ?>); return false; } catch (e) { }"<?php } ?>>마지막페이지</a><?php } ?><?php } public function printTail($p_intLine, $p_intCol) { ?><?php } } ?>